def generator = { String alphabet, int n ->
  new Random().with {
    (1..n).collect { alphabet[ nextInt( alphabet.length() ) ] }.join()
  }
}


def MAX_SIZE = 4
String myArray= '"'+generator ( ('0'..'9').join(), 10 )+'"'

for (i = 0; i < MAX_SIZE-1; i++) {
   myArray = String.format( "%s,%s",myArray, '"'+generator ( ('0'..'9').join(), 10 )+'"' )
}

return myArray

def generator = { String alphabet, int n ->
  new Random().with {
    (1..n).collect { alphabet[ nextInt( alphabet.length() ) ] }.join()
  }
}

def a = generator ( (('A'..'Z')+('0'..'9')).join(), 30 )
a= 'CAMP_' + a
return a
